/**
 * FlightDelay API microservice
 */
class FlightDelayInsurance {
  /**
   * Constructor
   */
  constructor() {
    this.name = `${process.env.npm_package_name}.v${process.env.npm_package_version}`;
  }

  /**
   * Process submitted application data
   * @param {string} clientId
   * @param {{}} payload
   */
  onApplied(clientId, payload) {
    // validate form data
    this.log.info('Form applied:', clientId, payload);

    this.dip.createPolicy(clientId, payload);
  }

  /**
   * Handle error
   * @param {string} client
   * @param {Error} error
   */
  onError(client, error) {
    this.log.error(client, error);
  }

  /**
   * Handle LogSetState event from contract
   * @param {string} policyId
   * @param {{}} payload
   * @return {Promise}
   */
  onLogSetState(policyId, payload) {
    // Applied
    if (payload.state === 0) return this.onPolicyAppliedState(policyId, payload);

    // Accepted
    if (payload.state === 1) return this.onPolicyAcceptedState(policyId, payload);

    // Revoked
    if (payload.state === 2) return this.onPolicyRevoked(policyId, payload);

    // PaidOut
    if (payload.state === 3) return this.onPolicyPaidOutState(policyId, payload);

    // Expired
    if (payload.state === 4) return this.onPolicyExpiredState(policyId, payload);

    // Declined
    if (payload.state === 5) return this.onPolicyDeclinedState(policyId, payload);

    // SendFailed
    if (payload.state === 6) return this.onPolicySendFailedState(policyId, payload);

    return null;
  }

  /**
   * Policy state handlers
   */

  /**
   * Handle applied policy state
   * @param {string} policyId
   */
  onPolicyAppliedState(policyId) {
    // Policy applied
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} applied` });
  }

  /**
   * Handle accepted policy state
   * @param {string} policyId
   */
  onPolicyAcceptedState(policyId) {
    // Policy accepted
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} underwritten by oracle` });

    this.dip.chargeCard(policyId);
  }

  /**
   * Handle revoked policy state
   * @param {string} policyId
   */
  onPolicyRevoked(policyId) {
    // Policy revoked
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} revoked` });
  }

  /**
   * Handle paidOut policy state
   * @param {string} policyId
   */
  onPolicyPaidOutState(policyId) {
    // Policy paidout
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} for payout` });
    this.dip.payout(policyId);
  }

  /**
   * Handle expired policy state
   * @param {string} policyId
   */
  onPolicyExpiredState(policyId) {
    // Policy expired
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} expired` });
  }

  /**
   * Handle declined policy state
   * @param {string} policyId
   */
  onPolicyDeclinedState(policyId) {
    // Policy declined
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} declined` });
  }

  /**
   * Handle send failed policy state
   * @param {string} policyId
   */
  onPolicySendFailedState(policyId) {
    // Policy send failed
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} send failed` });

    // this.dip.notifyAdministrator(policyId, { msg: 'Send failed' });
  }

  /**
   * App logic
   */

  /**
   * On card charged event handler
   * @param {string} policyId
   */
  onCardCharged(policyId) {
    this.dip.issueCertificate(policyId);
  }

  /**
   * On certificated issued event handler
   * @param {string} policyId
   */
  onCertificateIssued(policyId) {
    this.dip.send(policyId, { from: this.name, msg: `Policy ${policyId} accepted` });
  }
}

module.exports = FlightDelayInsurance;

const WebSocket = require('ws');
const uuid = require('uuid/v1');
const amqp = require('amqplib');
const http = require('http');


const shared = {
  exhanges: {
    policy: 'POLICY',
  },
  queues: {
    policyLog: 'policy.log',
    policyCreate: 'policy.create',
  },
  topic: {
    policyCreate: 'policy.create',
    policyCreationSuccess: 'policy.creation_success',
  },
};

/**
 * Logger
 */
class Logger {
  /**
   * Constructor
   */
  constructor() {
    this.info = console.log;
    this.error = console.error;
  }
}

/**
 * Generic Insurance application wrapper
 */
class GenericInsurance {
  /**
   * Constructor
   * @param {{}} app
   */
  constructor(app) {
    const logger = new Logger();

    this._app = app;
    this._app.dip = this;
    this._app.log = logger;

    this.log = logger;

    this._connections = {};

    this._amqp = null;
  }

  /**
   * Bootstrap and listen
   * @param {string} amqpBroker
   * @param {string} wsPort
   * @return {*}
   */
  async listen({ amqpBroker, wsPort }) {
    const conn = await amqp.connect(amqpBroker);

    this._amqp = await conn.createChannel();

    const server = http.createServer(((req, res) => {
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.write('{"status":200}');
      res.end();
    }));

    const wss = new WebSocket.Server({ server });

    server.listen(wsPort);

    wss.on('connection', ws => this.register(ws));

    await this._amqp.assertExchange(shared.exhanges.policy, 'topic', { durable: true });

    const q = await this._amqp.assertQueue(shared.queues.policyLog, { exclusive: false });
    await this._amqp.bindQueue(q.queue, shared.exhanges.policy, '#');

    await this._amqp.consume(q.queue, (message) => {
      console.log(`[READ]: ${message.fields.routingKey}: '${message.content.toString()}'`);

      this.send(message.properties.correlationId, {
        from: `${message.properties.headers.originatorName}.v${message.properties.headers.originatorVersion}`,
        topic: message.fields.routingKey,
        msg: message.content.toString(),
      });

      if (message.fields.routingKey === 'policy.state_changed.v1') {
        this._app.onLogSetState(message.properties.correlationId, {
          state: JSON.parse(message.content.toString()).state,
        });
      }

      if (message.fields.routingKey === 'policy.card_charged.v1') {
        this._app.onCardCharged(message.properties.correlationId, {});
      }

      if (message.fields.routingKey === 'policy.sertificate_issued.v1') {
        this._app.onCertificateIssued(message.properties.correlationId, {});
      }
    }, { noAck: true });


    this.log.info(`${this._app.name} listening at ws://localhost:${wsPort}/ws`);
  }

  /**
   * Registrer WebSocket connection
   * @param {{}} connection
   */
  register(connection) {
    const connectionId = uuid();

    this._connections[connectionId] = connection;

    this.send(connectionId, {
      from: `${process.env.npm_package_name}.v${process.env.npm_package_version}`,
      topic: null,
      msg: 'WebSocket connection successfully established',
    });

    connection.on('message', message => this.processMessage(connectionId, message));
  }

  /**
   * Process broker's message
   * @param {string} connectionId
   * @param {{}} message
   */
  processMessage(connectionId, message) {
    const payload = JSON.parse(message);

    if (payload.type === 'apply') {
      this._app.onApplied(connectionId, payload.data);
      this.send(connectionId, {
        from: `${process.env.npm_package_name}.v${process.env.npm_package_version}`,
        topic: null,
        msg: `Applied data: ${message}`,
      });
    }
  }

  /**
   * Send message to WebSocket client
   * @param {string} connectionId
   * @param {{}} msg
   */
  send(connectionId, msg) {
    this._connections[connectionId].send(JSON.stringify(msg));
  }

  /**
   * Send policy creation message to broker
   * @param {string} clientId
   * @param {{}} payload
   * @return {Promise<void>}
   */
  async createPolicy(clientId, payload) {
    const key = `${shared.topic.policyCreate}.v1`;

    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish(shared.exhanges.policy, key, Buffer.from(JSON.stringify(payload)), {
      correlationId: clientId,
      headers: {
        originatorName: process.env.npm_package_name,
        originatorVersion: process.env.npm_package_version,
      },
    });
  }

  /**
   * Send card charding message to broker
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async chargeCard(policyId) {
    const key = 'policy.charge_card.v1';

    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish(
      shared.exhanges.policy,
      key,
      Buffer.from(JSON.stringify({ policyId })),
      {
        correlationId: policyId,
        headers: {
          originatorName: process.env.npm_package_name,
          originatorVersion: process.env.npm_package_version,
        },
      },
    );
  }

  /**
   * Send fiat payout message to broker
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async payout(policyId) {
    const key = 'policy.payout.v1';

    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish(
      shared.exhanges.policy,
      key,
      Buffer.from(JSON.stringify({ policyId })),
      {
        correlationId: policyId,
        headers: {
          originatorName: process.env.npm_package_name,
          originatorVersion: process.env.npm_package_version,
        },
      },
    );
  }

  /**
   * Send certificate issuing message to broker
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async issueCertificate(policyId) {
    const key = 'policy.issue_certificate.v1';

    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish(
      shared.exhanges.policy,
      key,
      Buffer.from(JSON.stringify({ policyId })),
      {
        correlationId: policyId,
        headers: {
          originatorName: process.env.npm_package_name,
          originatorVersion: process.env.npm_package_version,
        },
      },
    );
  }
}

