/**
 * Decode hex to string
 * @param {string} value
 * @return {string}
 */
function hexToUtf8(value) {
  const hex = value.toString();

  let str = '';
  for (let i = 0; (i < hex.length && hex.substr(i, 2) !== '00'); i += 2) {
    str += String.fromCharCode(parseInt(hex.substr(i, 2), 16));
  }

  return str;
}

