/**
 * FlightDelay API microservice
 */
class FlightDelayInsurance {
  /**
   * Constructor
   */
  constructor() {
    this.name = `${process.env.npm_package_name}.v${process.env.npm_package_version}`;
  }

  /**
   * Process submitted application data
   * @param {string} clientId
   * @param {{}} payload
   */
  onApplied(clientId, payload) {
    // validate form data
    this.log.info('Form applied:', clientId, payload);

    this.dip.createPolicy(clientId, payload);
  }

  /**
   * Handle error
   * @param {string} client
   * @param {Error} error
   */
  onError(client, error) {
    this.log.error(client, error);
  }

  /**
   * Handle LogSetState event from contract
   * @param {{}} params
   * @param {{}} params.content
   * @param {{}} params.fields
   * @param {{}} params.properties
   * @return {Promise}
   */
  onLogSetState({ content, fields, properties }) {
    // Applied
    if (content.state === 0) return this.onPolicyAppliedState(properties.correlationId, content);

    // Accepted
    if (content.state === 1) return this.onPolicyAcceptedState(properties.correlationId, content);

    // Revoked
    if (content.state === 2) return this.onPolicyRevoked(properties.correlationId, content);

    // PaidOut
    if (content.state === 3) return this.onPolicyPaidOutState(properties.correlationId, content);

    // Expired
    if (content.state === 4) return this.onPolicyExpiredState(properties.correlationId, content);

    // Declined
    if (content.state === 5) return this.onPolicyDeclinedState(properties.correlationId, content);

    // SendFailed
    if (content.state === 6) return this.onPolicySendFailedState(properties.correlationId, content);

    return null;
  }

  /**
   * Policy state handlers
   */

  /**
   * Handle applied policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyAppliedState(correlationId, payload) {
    // Policy applied
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} applied` });
  }

  /**
   * Handle accepted policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyAcceptedState(correlationId, payload) {
    // Policy accepted
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} underwritten by oracle` });

    this.dip.chargeCard(correlationId, payload.policyId);
  }

  /**
   * Handle revoked policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyRevoked(correlationId, payload) {
    // Policy revoked
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} revoked` });
  }

  /**
   * Handle paidOut policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyPaidOutState(correlationId, payload) {
    // Policy paidout
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} for payout` });
    this.dip.payout(correlationId, payload.policyId);
  }

  /**
   * Handle expired policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyExpiredState(correlationId, payload) {
    // Policy expired
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} expired` });
  }

  /**
   * Handle declined policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicyDeclinedState(correlationId, payload) {
    // Policy declined
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} declined` });
  }

  /**
   * Handle send failed policy state
   * @param {string} correlationId
   * @param {{}} payload
   */
  onPolicySendFailedState(correlationId, payload) {
    // Policy send failed
    this.dip.send(correlationId, { from: this.name, msg: `Policy ${payload.policyId} send failed` });

    // this.dip.notifyAdministrator(policyId, { msg: 'Send failed' });
  }

  /**
   * App logic
   */

  /**
   * On card charged event handler
   * @param {{}} params
   * @param {{}} params.content
   * @param {{}} params.fields
   * @param {{}} params.properties
   */
  onCardCharged({ content, fields, properties }) {
    this.dip.issueCertificate(properties.correlationId, content.policyId);
  }

  /**
   * On certificated issued event handler
   * @param {{}} params
   * @param {{}} params.content
   * @param {{}} params.fields
   * @param {{}} params.properties
   */
  onCertificateIssued({ content, fields, properties }) {
    this.dip.send(properties.correlationId, { from: this.name, msg: `Policy ${content.policyId} accepted` });
  }
}

module.exports = FlightDelayInsurance;

const WebSocket = require('ws');
const uuid = require('uuid/v1');
const http = require('http');


/**
 * Generic Insurance application wrapper
 */
class GenericInsurance {
  /**
   * Constructor
   * @param {string} amqp
   * @param {string} config
   * @param {string} log
   */
  constructor({ amqp, config, log }) {
    this._wsPort = config.wsPort;
    this._app = config.app;
    this._app.dip = this;
    this._app.log = log;

    this.log = log;

    this._connections = {};

    this._amqp = amqp;
  }

  /**
   * Bootstrap and listen
   * @return {*}
   */
  async bootstrap() {
    const server = http.createServer(((req, res) => {
      res.writeHead(200, { 'Content-Type': 'application/json' });
      res.write('{"status":200}');
      res.end();
    }));

    const wss = new WebSocket.Server({ server });

    server.listen(this._wsPort);

    wss.on('connection', ws => this.register(ws));

    await this._amqp.consume({
      messageType: '*',
      messagetypeVersion: '#',
      handler: ({ content, fields, properties }) => {
        this.send(properties.correlationId, {
          from: `${properties.headers.originatorName}.v${properties.headers.originatorVersion}`,
          topic: fields.routingKey,
          msg: JSON.stringify(content),
        });
      },
    });

    await this._amqp.consume({
      messageType: 'stateChanged',
      messageVersion: '1.*',
      handler: this._app.onLogSetState.bind(this._app),
    });

    await this._amqp.consume({
      messageType: 'cardCharged',
      messageVersion: '1.*',
      handler: this._app.onCardCharged.bind(this._app),
    });

    await this._amqp.consume({
      messageType: 'certificateIssued',
      messageVersion: '1.*',
      handler: this._app.onCertificateIssued.bind(this._app),
    });

    this.log.info(`${this._app.name} listening at ws://localhost:${this._wsPort}/ws`);
  }

  /**
   * Registrer WebSocket connection
   * @param {{}} connection
   */
  register(connection) {
    const connectionId = uuid();

    this._connections[connectionId] = connection;

    this.send(connectionId, {
      from: `${process.env.npm_package_name}.v${process.env.npm_package_version}`,
      topic: null,
      msg: 'WebSocket connection successfully established',
    });

    connection.on('message', message => this.processMessage(connectionId, message));
  }

  /**
   * Process broker's message
   * @param {string} connectionId
   * @param {{}} message
   */
  processMessage(connectionId, message) {
    const payload = JSON.parse(message);

    if (payload.type === 'apply') {
      this._app.onApplied(connectionId, payload.data);
      this.send(connectionId, {
        from: `${process.env.npm_package_name}.v${process.env.npm_package_version}`,
        topic: null,
        msg: `Applied data: ${message}`,
      });
    }
  }

  /**
   * Send message to WebSocket client
   * @param {string} connectionId
   * @param {{}} msg
   */
  send(connectionId, msg) {
    if (!connectionId || !this._connections[connectionId]) return;
    this._connections[connectionId].send(JSON.stringify(msg));
  }

  /**
   * Send policy creation message to broker
   * @param {string} clientId
   * @param {{}} payload
   * @return {Promise<void>}
   */
  async createPolicy(clientId, payload) {
    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish({
      messageType: 'policyCreationRequest',
      messageVersion: '1.*',
      content: payload,
      correlationId: clientId,
    });
  }

  /**
   * Send card charding message to broker
   * @param {string} correlationId
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async chargeCard(correlationId, policyId) {
    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish({
      messageType: 'chargeCard',
      messageVersion: '1.*',
      content: { policyId },
      correlationId,
    });
  }

  /**
   * Send fiat payout message to broker
   * @param {string} correlationId
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async payout(correlationId, policyId) {
    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish({
      messageType: 'payout',
      messageVersion: '1.*',
      content: { policyId },
      correlationId,
    });
  }

  /**
   * Send certificate issuing message to broker
   * @param {string} correlationId
   * @param {string} policyId
   * @return {Promise<void>}
   */
  async issueCertificate(correlationId, policyId) {
    // Todo: implement
    await new Promise(resolve => setTimeout(resolve, 1000));

    await this._amqp.publish({
      messageType: 'issueCertificate',
      messageVersion: '1.*',
      content: { policyId },
      correlationId,
    });
  }
}

