const Knex = require('knex');

/**
 * Database
 */
class Database {
  /**
   * Constructor
   * @param {Knexfile} options
   */
  constructor(options) {
    this.options = options;

    this._connection = null;
  }

  /**
   * Get database connection
   * @return {Knex}
   */
  getConnection() {
    return this._connection;
  }

  /**
   * Create database connection
   * @return {Knex}
   */
  createConnection() {
    this._connection = Knex(this.options);

    return this._connection;
  }

  /**
   * Start module
   * @return {Promise<void>}
   */
  async bootstrap() {
    await this.createConnection();

    await this._connection.raw('select 1+1 as result').catch((e) => {
      const error = e;
      error.exit = 1;

      throw error;
    });
  }

  /**
   * Shutdown module
   */
  shutdown() {
    if (this._connection) {
      this._connection.destroy();
    }
  }
}

module.exports = Database;

const SERVER_IS_READY = 'SERVER_IS_READY';
const SERVER_IS_NOT_READY = 'SERVER TEMPORARILY UNAVAILABLE';
const SERVER_IS_SHUTTING_DOWN = 'SERVER_IS_SHUTTING_DOWN';
const SERVER_IS_NOT_SHUTTING_DOWN = 'SERVER_IS_LIVE';

/**
 * Routes for Kubernetes
 */
class K8sController {
  /**
   * Constructor
   */
  constructor() {
    this.shuttingDownStatus = false;
    this.readyStatus = false;

    this.ready = this.ready.bind(this);
    this.live = this.live.bind(this);
    this.version = this.version.bind(this);
  }

  /**
   * Set health status
   * @param {boolean} status
   */
  setShuttingDownStatus(status = true) {
    this.shuttingDownStatus = status;
  }

  /**
   * Set ready status
   * @param {boolean} status
   */
  setReadyStatus(status = true) {
    this.readyStatus = status;
  }

  /**
   * Get application health status
   * @param {Context} ctx
   * @return {Promise<void>}
   */
  async health(ctx) {
    let status;
    let message;

    if (this.shuttingDown) {
      status = 500;
      message = SERVER_IS_SHUTTING_DOWN;
    } else if (this.readyStatus) {
      status = 200;
      message = SERVER_IS_READY;
    } else {
      status = 500;
      message = SERVER_IS_NOT_READY;
    }

    ctx.type = 'text/plain';
    ctx.status = status;
    ctx.body = message;
  }


  /**
   * Is service live
   * @param {Context} ctx
   * @return {Promise<void>}
   */
  async live(ctx) {
    let status = 200;
    let message;

    if (this.shuttingDownStatus) {
      status = 500;
      message = SERVER_IS_SHUTTING_DOWN;
    } else {
      status = 200;
      message = SERVER_IS_NOT_SHUTTING_DOWN;
    }

    ctx.type = 'text/plain';
    ctx.status = status;
    ctx.body = message;
  }

  /**
   * Is service ready to accept requests
   * @param {Context} ctx
   * @return {Promise<void>}
   */
  async ready(ctx) {
    let status;
    let message;

    if (this.readyStatus) {
      message = SERVER_IS_READY;
      status = 200;
    } else {
      message = SERVER_IS_NOT_READY;
      status = 500;
    }

    ctx.type = 'text/plain';
    ctx.status = status;
    ctx.body = message;
  }

  /**
   * Get application version
   * @param {Content} ctx
   * @return {Promise<void>}
   */
  async version(ctx) {
    ctx.body = {
      name: process.env.npm_package_name,
      version: process.env.npm_package_version,
    };
  }
}

module.exports = K8sController;

module.exports = async function exceptionHandler(ctx, next) {
  try {
    await next();
  } catch (err) {
    ctx.status = 500;

    if (process.env.NODE_ENV !== 'production') {
      ctx.body = ctx.body || { error: err.toString() };
    }

    ctx.app.emit('error', err, ctx);
  }
};

const Koa = require('koa');
const Logger = require('koa-logger');
const Cors = require('kcors');
const BodyParser = require('koa-bodyparser');
const prometheus = require('@echo-health/koa-prometheus-exporter');
const exceptionHandler = require('./exceptionHandler');
const router = require('./router');
const K8sController = require('./controllers/k8sController');

/**
 * Http server
 */
class HttpApp extends Koa {
  /**
   * Constructor
   * @param {number} port
   */
  constructor(port) {
    super();

    this._server = null;

    this._port = port;

    this._controllers = {};
  }

  /**
   * Get instance of server
   * @return {null|*}
   */
  get server() {
    return this._server;
  }

  /**
   * Get server port
   * @return {number}
   */
  get port() {
    return this._port;
  }

  /**
   * Shutdown http server
   */
  shutdown() {
    if (this._server) {
      this._server.close();
    }
  }

  /**
   * Set application readyness status
   * @param {boolean} status
   */
  setReadyStatus(status) {
    const { k8sController } = this._controllers;

    if (!k8sController) {
      throw new Error({
        message: 'k8sController does not exists',
        exit: 1,
      });
    }

    k8sController.setReadyStatus(status);
  }

  /**
   * Get application shutdown status
   * @return {boolean}
   */
  getShuttingDownStatus() {
    const { k8sController } = this._controllers;

    if (!k8sController) {
      throw new Error({
        message: 'k8sController does not exists',
        exit: 1,
      });
    }

    return k8sController.shuttingDownStatus;
  }

  /**
   * Set application shutdown status
   * @param {boolean} status
   */
  setShuttingDownStatus(status) {
    const { k8sController } = this._controllers;

    if (!k8sController) {
      throw new Error({
        message: 'k8sController does not exists',
        exit: 1,
      });
    }

    k8sController.setShuttingDownStatus(status);
  }

  /**
   * Start http server
   * @param {Router} appRouter
   * @return {Promise}
   */
  bootstrap(appRouter) {
    this._controllers.k8sController = new K8sController();
    const coreRouter = router(this._controllers);

    this
      .use(exceptionHandler)
      .use(new Logger())
      .use(new Cors())
      .use(new BodyParser())
      .use(appRouter.routes())
      .use(coreRouter.routes())
      .use(prometheus.httpMetricMiddleware())
      .use(prometheus.middleware({}));

    return new Promise((resolve, reject) => {
      this._server = this.listen(this._port, (err) => {
        if (err) {
          reject(new Error(err));
          return;
        }

        resolve({
          host: 'http://localhost',
          port: this._port,
        });
      });
    });
  }
}

module.exports = HttpApp;

