const AWS = require('aws-sdk');

/**
 * Database
 */
class S3 {
  /**
   * Constructor
   * @param {options} options
   */
  constructor(options) {
    this.options = options;

    this.client = null;
  }

  /**
   * Start module
   * @param {string} bucket
   * @return {Promise<void>}
   */
  async bootstrap(bucket) {
    this.bucket = bucket;
    this.client = new AWS.S3(this.options);
    if (!this.bucket) return;
    await this.client.createBucket({ Bucket: this.bucket }).promise()
      .catch((e) => {
        const error = new Error(JSON.stringify({ message: e.message, stack: e.stack }));
        if (e.code !== 'BucketAlreadyOwnedByYou') {
          error.exit = true;
          throw (error);
        }
      });
  }

  /**
   * Send artifact list
   * @param {string} product
   * @param {string} network
   * @return {string}
   */
  async getLastVersion(product, network) {
    const prefix = `${product}/${network}/`;
    const response = await this.client.listObjects({
      Bucket: this.bucket,
      Prefix: prefix,
    }).promise();
    return response.Contents
      .map(o => o.Key.replace(prefix, '').replace(/(.+)\/.+/, '$1'))
      .sort()
      .slice(-1)[0];
  }

  /**
   * Getting artifacts
   * @param {string} product
   * @param {string} network
   * @param {string} version
   * @return {array<buffer>}
   */
  async getArtifacts(product, network, version) {
    const prefix = `${product}/${network}/${version || await this.getLastVersion(product, network)}/`;
    const list = await this.client.listObjects({
      Bucket: this.bucket,
      Delimiter: '/',
      Prefix: prefix,
    }).promise();
    return Promise.all(
      list.Contents.map(async (o) => {
        const object = await this.client.getObject({
          Bucket: this.bucket,
          Key: o.Key,
        }).promise();
        return {
          name: o.Key.replace(prefix, ''),
          content: JSON.parse(object.Body.toString()),
        };
      }),
    );
  }

  /**
   * Shutdown module
   */
  shutdown() {
    if (this.client) {
      this.client = null;
    }
  }
}

module.exports = S3;

const WS = require('ws');
const uuid = require('uuid/v1');

/**
 * WebSocket IO
 */
class WebSocket {
  /**
   * Constructor
   * @param {{}} http
   * @param {{}} config
   * @param {{}} log
   */
  constructor({ server, config, log }) {
    this._server = server;
    this._config = config;
    this._log = log;
    this._connections = {};
  }

  /**
   * Start
   */
  async bootstrap() {
    this._wss = new WS.Server({ server: this._server });

    this._wss.on('connection', ws => this._register(ws));
  }

  /**
   * Register new WebSocket connection
   * @param {{}} connection
   * @private
   */
  _register(connection) {
    const connectionId = uuid();

    this._connections[connectionId] = connection;

    this.send(connectionId, {
      microservice: `${this._config.appName}.v${this._config.appVersion}`,
      topic: null,
      msg: 'WebSocket connection successfully established',
    });

    connection.on('message', message => this._handleMessage(connectionId, message));
  }


  /**
   * Handle WebSocker message
   * @param {string} connectionId
   * @param {string} message
   * @private
   */
  _handleMessage(connectionId, message) {
    this._log.info(connectionId, message);

    if (this._handler) {
      this._handler(connectionId, message);
    } else {
      this._log.error('Handler for WebSocket messages is not defined');
    }
  }

  /**
   * Bind external handler for processing WebSocket messages
   * @param {function} handler
   */
  setHandler(handler) {
    this._handler = handler;
  }

  /**
   * Send message to WebSocker connection
   * @param {string} connectionId
   * @param {{}} msg
   */
  send(connectionId, msg) {
    if (!connectionId) return;
    this._connections[connectionId].send(JSON.stringify(msg));
  }
}

module.exports = WebSocket;

